"use strict";

/**
 * Yields all the accumulations of the iterator with a given accumulator method.
 *
 * @type rT The item type of the returned iterator.
 * @type iT The item type of the input iterator.
 * @this {Iterable<iT>}
 * @ntime O(n)
 * @dspace O(1)
 * @example Basic Usage
 *
 * ```javascript
 * [1,2,3]::aggregate(function (b) {
 *   return this + b;
 * }, 0) // yields 0, 1, 3, 6
 * ```
*/
export function * aggregate <rT, iT> (
    accumulator : (_this : rT, item : iT) => rT,
    accumulation : rT,
) : Iterable<rT> {
    for ( const item of this ) {
        yield accumulation;
        accumulation = accumulation::accumulator(item);
    }

    yield accumulation;
};

"use strict";

/**
 * Maps the iterator with the additional tail of `k` previous items.
 * `k` is determined by the number of items in the accumulation iterator.
 *
 * @type rT The item type of the returned iterator.
 * @type iT The item type of the input iterator.
 * @this {Iterable<iT>}
 * @ntime O(n)
 * @dspace O(k)
 * @example Basic Usage
 *
 * ```javascript
 * [1,2,3].convolve(function (tail) {
 *   return [...tail, this].reduce(add);
 * }, [0, -1]) // yields 0, 3, 6
 * ```
*/
export function * convolve <rT, iT> (
    accumulator : (_this : iT, tail : iT) => rT,
    tail : Iterable<iT>,
) : Iterable<rT> {
    let currentTail = [...tail];

    for ( const item of this ) {
        const result = item::accumulator(currentTail);
        currentTail = [item].concat(currentTail.slice(0, tail.length - 1));
        yield result;
    }
};

"use strict";

/**
 * Yields the number of items in the iterator.
 *
 * @this {Iterable<any>}
 * @ntime O(n)
 * @dspace O(1)
 * @example Basic Usage
 *
 * ```javascript
 * [1,2,3]::count() // yields 3
 * ```
*/
export function * count (
) : Iterable<T> {
    let count = 0;

    for ( const item of this ) {
        count++;
    }

    yield count;
};

"use strict";

/**
 * Yields the sorted difference iterator of two sorted iterators.
 *
 * @this {Iterable<T>}
 * @param comparator The sorting value function.
 * Should return `0` when items are equal, a positive number when the item
 * on the left is greater and a negative number when the item on the right is greater.
 * @ntime O(n+m)
 * @dspace O(2)
 * @example Basic Usage
 *
 * ```javascript
 * [1,2,4]::difference([2,3,4,5], function (b) {
 *   return this - b;
 * }); // yields 1,3,5
 * ```
*/
export function * difference <T> (
    b : Iterable<T>,
    comparator : (_this: T, item: T) => number,
) : Iterable<T> {
    const iteratorA = this[Symbol.iterator]();
    const iteratorB = b[Symbol.iterator]();
    let stepA = iteratorA.next();
    let stepB = iteratorB.next();

    while ( !stepA.done && !stepB.done ) {
        const comparison = stepA.value::comparator(stepB.value);

        if ( comparison < 0 ) {
            yield stepA.value;
            stepA = iteratorA.next();
        } else if ( comparison > 0 ) {
            yield stepB.value;
            stepB = iteratorB.next();
        } else {
            stepA = iteratorA.next();
            stepB = iteratorB.next();
        }
    }

    if ( !stepA.done ) {
        yield stepA.value;
        yield * iteratorA;
    } else if ( !stepB.done ) {
        yield stepB.value;
        yield * iteratorB;
    }
};

"use strict";

/**
 * Yields the items that don't qualify the condition.
 *
 * @this {Iterable<T>}
 * @ntime O(n)
 * @dspace O(1)
 * @example Basic Usage
 *
 * ```javascript
 * [1,2,3,4]::drop(function () {
 *   return this % 2 === 0;
 * }) // yields 1, 3
 * ```
*/
export function * drop <T> (
    condition : (_this : T) => boolean,
) : Iterator<T> {
    for ( const item of this ) {
        if ( !item::condition() ) { yield item; }
    }
};

"use strict";

/**
 * Yields the first item that matches condition, and all the items after that.
 *
 * @this {Iterable<T>}
 * @param condition A condition called on every item to see if it should be dropped.
 * @ntime O(n)
 * @dspace O(1)
 * @example Basic Usage
 *
 * ```javascript
 * [1,2,3,4,1,2,3,4]::dropWhile(function () {
 *   return this &lt; 4;
 * }) // yields 4, 1, 2, 3, 4
 * ```
*/
export function * dropWhile <T> (
    condition : (_this : T) => boolean,
) : Iterable<T> {
    const iterator = this[Symbol.iterator]();

    for ( const item of iterator ) {
        if ( item::condition() ) { continue; }
        yield item;
    }

    yield * iterator;
};

"use strict";

/**
 * Yields the first item that qualifies the condition.
 *
 * @this {Iterable<T>}
 * @ntime O(n)
 * @dspace O(1)
 * @example Basic Usage
 *
 * ```javascript
 * [{id:2,x:1}, {id:3,x:2}, {id:4,x:4}, {id:3,x:5}]::find(function () {
 *   return this.id === 3;
 * }) // yields { id: 3, x: 2 }
 * ```
*/
export function * find <T> (
    condition : (_this : T) => boolean,
) : Iterable<T> {
    for ( const item of this ) {
        if ( item::condition() ) {
            yield item;
            return;
        }
    }
};

"use strict";

/**
 * Yields the k-th item of the iterator.
 *
 * @this {Iterable<T>}
 * @ntime O(k)
 * @dspace O(1)
 * @example Basic Usage
 *
 * ```javascript
 * [1,2,3]::first(0) // yields 1
 * [1,2,3]::first(1) // yields 2
 * [1,2,3]::first(2) // yields 3
 * [1,2,3]::first(3) // yields nothing
 * ```
*/
export function * first <T> (
    k : number,
) : Iterable<T> {
    let index = 0;

    for ( const item of this ) {
        if ( index++ === k ) {
            yield item;
            return;
        }
    }
};

"use strict";

/**
 * Forks the iterator and yields the forks.
 *
 * @this {Iterable<T>}
 * @example Basic Usage
 *
 * ```javascript
 * const source = [1,2,3]::map(function () { return this * 2});
 * const forks = source::fork();
 * const a = forks.next().value;
 * const b = forks.next().value;
 * [...a] // [1,2,3]
 * [...b] // [1,2,3]
 * ```
*/
export function * fork <T> (

) : Iterable<Iterable<T>> {
    const source = this[Symbol.iterator]();
    const indices = [];
    let buffer = [];
    let readIndex = 0;
    let done = false;

    while ( true ) {
        if ( readIndex > 0 ) {
            throw new TypeError("Can't fork the iterator after the iteration has started");
        }

        const iteratorIndex = indices.length;
        indices.push(0);

        yield function * () {
            while ( true ) {
                const offset = indices[iteratorIndex] - readIndex;

                if ( offset < 0 ) {
                    indices[iteratorIndex] = readIndex;
                    const chunk = buffer.slice(offset);
                    const lowestOffset = Math.min(...indices) - readIndex;

                    if ( lowestOffset < 0 ) {
                        buffer = buffer.slice(lowestOffset);
                    } else {
                        buffer = [];
                    }

                    yield * chunk;
                    continue;
                }

                if ( done ) { return; }

                const step = source.next();
                done = step.done;

                if ( done ) { return; }

                buffer.push(step.value);
                readIndex += 1;
            }
        }();
    }
};

