"use strict";

/**
 * Yields the k first items of the iterator.
 *
 * @this {Iterable<T>}
 * @ntime O(k)
 * @dspace O(k)
 * @example Basic Usage
 *
 * ```javascript
 * [1,2,3]::head(1) // yields 1
 * [1,2,3]::head(2) // yields 1, 2
 * [1,2,3]::head(3) // yields 1, 2, 3
 * [1,2,3]::head(4) // yields 1, 2, 3
 * ```
*/
export function * head <T> (
    k : number,
) : Iterable<T> {
    let index = 0;

    for ( const item of this ) {
        if ( index++ >= k ) { return; }
        yield item;
    }
};

"use strict";

/**
 * Yields the sorted intersection iterator of two sorted iterators.
 *
 * @this {Iterable<T>}
 * @param comparator The sorting value function. Should return `0` when items are equal, a positive number when the item on the left is greater and a negative number when the item on the right is greater.
 * @ntime O(n+m)
 * @dspace O(2)
 * @example Basic Usage
 *
 * ```javascript
 * [1,2,3,4,5]::intersection([2,3,4,6], function (b) {
 *   return this - b;
 * }); // yields 2,3,4
 * ```
*/
export function * intersection <T> (
    b : Iterable<T>,
    comparator : (_this: T, item: T) => number,
) : Iterable<T> {
    const iteratorA = this[Symbol.iterator]();
    const iteratorB = b[Symbol.iterator]();
    let stepA = iteratorA.next();
    let stepB = iteratorB.next();

    while ( !stepA.done && !stepB.done ) {
        const comparison = stepA.value::comparator(stepB.value);

        if ( comparison < 0 ) {
            stepA = iteratorA.next();
        } else if ( comparison > 0 ) {
            stepB = iteratorB.next();
        } else {
            yield stepA.value;
            stepA = iteratorA.next();
            stepB = iteratorB.next();
        }
    }
};

"use strict";

/**
 * Yields the k-th item from the end of the iterator.
 *
 * @this {Iterable<T>}
 * @ntime O(n)
 * @dspace O(k)
 * @example Basic Usage
 *
 * ```javascript
 * [1,2,3]::last(0) // yields 3
 * [1,2,3]::last(1) // yields 2
 * [1,2,3]::last(2) // yields 1
 * [1,2,3]::last(3) // yields nothing
 * ```
*/
export function * last <T> (
    k : number,
) : Iterable<T> {
    let buffer = [];

    for ( const item of this ) {
        buffer = buffer
            .concat([item])
            .slice(-(k + 1));
    }

    if ( buffer.length === k + 1 ) {
        yield buffer[0];
    }
};

"use strict";

/**
 * Yields the items of the iterator mapped through a given transformer.
 *
 * @type iT The item type of the input iterator.
 * @type rT The item type of the returned iterator.
 * @this {Iterable<iT>}
 * @ntime O(n)
 * @dspace O(1)
 * @example Basic Usage
 *
 * ```javascript
 * [1,2,3]::map(function () {
 *   return this * 2;
 * }) // yields 2, 4, 6
 * ```
*/
export function * map <iT, rT> (
    transformer : (_this : iT) => rT,
) : Iterable<rT> {
    for ( const item of this ) {
        yield item::transformer();
    }
};

"use strict";

/**
 * A pure JS alternative to the builtin sort - operates directly on iterators, so it can be advantageous to native sort in cases where
 * you only need a few of the sorted items, e.g. 15 first products sorted
 * by price. The first items get sorted precisely first so the whole set
 * does not necessarily need to be sorted.
 *
 * @this {Iterable<T>}
 * @ntime O(nÂ²)
 * @dspace O(n)
 * @example Basic Usage
 *
 * ```javascript
 * [3,2,5,1,4]::quickSort(function (b) {
 *   return this - b;
 * }); // yields 1,2,3,4,5
 * ```
*/
export function * quickSort <T> (
    comparator : (_this: T, b : T) => number,
) : Iterable<T> {
    const iterator = this[Symbol.iterator]();
    const smaller = [];
    const greater = [];
    const first = iterator.next();

    if ( first.done ) { return; }

    const pivot = first.value;

    for ( const item of iterator ) {
        const comparison = pivot::comparator(item);

        if ( comparison > 0 ) {
            smaller.push(item);
        } else {
            greater.push(item);
        }
    }

    yield * smaller::quickSort(comparator);
    yield pivot;
    yield * greater::quickSort(comparator);
};

"use strict";

import { aggregate } from "./aggregate";
import { last } from "./last";

/**
 * Yields the accumulation of the iterator with a given accumulator method.
 *
 * @type rT The item type of the returned iterator.
 * @type iT The item type of the input iterator.
 * @this {Iterable<iT>}
 * @ntime O(n)
 * @dspace O(1)
 * @example Basic Usage
 *
 * ```javascript
 * [3,2,5,1,4]::reduce(function (b) {
 *   return this + b;
 * }, 0); // yields 15
 * ```
*/
export function * reduce <rT, iT> (
    accumulator : (_this : rT, item : iT) => rT,
    accumulation : rT,
) : Iterable<rT> {
    yield * this
        ::aggregate(accumulator, accumulation)
        ::last(0)
        ;
};

"use strict";

/**
 * Yields the items of the iterator in reverse order.
 *
 * @this {Iterable<T>}
 * @ntime O(n)
 * @dspace O(2n)
 * @example Basic Usage
 *
 * ```javascript
 * [5,4,1,2,3]::reverse() // yields 3, 2, 1, 4, 5
 * ```
*/
export function * reverse <T> (

) : Iterable<T> {
    yield * [...this].reverse();
};

"use strict";

/**
 * Yields the items of the iterator sorted by a given comparator.
 *
 * @this {Iterable<T>}
 * @ntime Engine-specific
 * @dspace Engine-specific
 * @example Basic Usage
 *
 * ```javascript
 * [3,2,1].sort(function (b) { return this - b; }) // yields 1, 2, 3
 * ```
*/
export function * sort <T> (
    comparator : (_this : T, b : T) => number,
) : Iterable<T> {
    yield * [...this].sort((a, b) => a::comparator(b));
};

"use strict";

/**
 * Yields the items of the iterator sorted by a given comparator.
 *
 * @this {Iterable<T>}
 * @ntime Engine-specific
 * @dspace Engine-specific
 * @example Basic Usage
 *
 * ```javascript
 * ["foo", "bar"]::sortAlphabetically() // yields "bar", "foo"
 * "cba"::sortAlphabetically() // yields "a", "b", "c"
 * ```
*/
export function * sortAlphabetically <T> (

) : Iterable<T> {
    yield * [...this].sort();
};

"use strict";

import { sortAlphabetically } from "./sortAlphabetically";
import { map } from "./map";

/**
 * Yields the items of the iterator sorted by a given comparator.
 *
 * @this {Iterable<T>}
 * @ntime Engine-specific
 * @dspace Engine-specific
 * @example Basic Usage
 *
 * ```javascript
 * [{
 *   value: "foo",
 * }, {
 *   value: "bar",
 * }]::sortAlphabeticallyBy(function () {
 *   return this.value;
 * }) // yields { value: "bar" }, { value: "foo" }
 * ```
*/
export function * sortAlphabeticallyBy <T> (
    transformer : (_this : T) => string,
) : Iterable<T> {
    yield * this
        ::map(function () {
            const string = this::transformer();
            return { value: this, toString: () => string };
        })
        ::sortAlphabetically()
        ::map(function () {
            return this.value;
        });
};

