"use strict";

/**
 * Yields the k last items of the iterator.
 *
 * @this {Iterable<T>}
 * @ntime O(n)
 * @dspace O(k)
 * @example Basic Usage
 *
 * ```javascript
 * [1,2,3]::tail(1) // yields 3
 * [1,2,3]::tail(2) // yields 2, 3
 * [1,2,3]::tail(3) // yields 1, 2, 3
 * [1,2,3]::tail(4) // yields 1, 2, 3
 * ```
*/
export function * tail <T> (
    k : number,
) : Iterable<T> {
    let buffer = [];

    for ( const item of this ) {
        buffer = buffer
            .concat([item])
            .slice(-k);
    }

    yield * buffer;
};

"use strict";

/**
 * Yields the items that qualify the condition.
 *
 * @this {Iterable<T>}
 * @param condition A condition function to see if the item should be taken.
 * @ntime O(n)
 * @dspace O(1)
 * @example Basic Usage
 *
 * ```javascript
 * [1,2,3,4]
 *   ::take(function () {
 *     return this % 2 === 0;
 *   }) // yields 2 and 4
 * ```
*/
export function * take <T> (
    condition : (_this : T) => boolean,
) : Iterable<T> {
    for ( const item of this ) {
        if ( item::condition() ) { yield item; }
    }
};

"use strict";

/**
 * Yields the items until an item that matches the condition is found.
 *
 * @this {Iterable<T>}
 * @param condition A condition called on every item to see if it should be taken.
 * @ntime O(n)
 * @dspace O(1)
 * @example Basic Usage
 *
 * ```javascript
 * [1,2,3,4,1,2,3,4]::takeWhile(function () {
 *   return this &lt; 4;
 * }) // yields 1, 2, 3
 * ```
*/
export function * takeWhile <T> (
    condition : (_this : T) => boolean,
) : Iterable<T> {
    for ( const item of this ) {
        if ( !item::condition() ) { return; }
        yield item;
    }
};

"use strict";

/**
 * Unwraps the iterator into a collection.
 *
 * @this {Iterable<iT>}
 * @param Type The constructor for the collection type.
 * @example Arrays
 *
 * ```javascript
 * [1,2,3].map(function () {
 *   return this * 2;
 * })::to(Array) // returns [2, 4, 6]
 * ```
 *
 * @example Objects
 *
 * ```javascript
 * [{id: "a"}, {id: "b"}].map(function () {
 *   return [this.id, this];
 * })::to(Object); // returns { a: { id: "a" }, b: { id: "b"} }
 * ```
 *
 * @example Other Collections
 *
 * ```javascript
 * [2.5, 3.5]::to(Uint16Array) // returns Uint16Array [2, 3]
 * ["foo", "bar", "foo"]::to(Set) // returns Set ["foo", "bar"]
 * [["x", "y"], ["z", "n"]]::to(Map) // returns Map { "x": "y", "z": "n" }
 * ```
*/
export function to <iT, rT> (
    Type : Function,
) : rT {
    if ( Type === Array ) {
        return [...this];
    } else if ( Type === Object ) {
        return [...this].reduce((accumulation, entry) =>
            ({ ...accumulation, [entry[0]]: entry[1] }), {});
    } else {
        return new Type([...this]);
    }
};

