"use strict";

/**
 * Yields the sorted union iterator of two sorted iterators.
 *
 * @this {Iterable<T>}
 * @param comparator The sorting value function. Should return `0` when items are equal, a positive number when the item on the left is greater and a negative number when the item on the right is greater.
 * @ntime O(n+m)
 * @dspace O(2)
 * @example Basic Usage
 *
 * ```javascript
 * [2,3,4,7,8,9]::union([1,5,6,7], function (b) {
 *   return this - b;
 * }); // yields 1,2,3,4,5,6,7,8,9
 * ```
*/
export function * union <T> (
    b : Iterable<T>,
    comparator : (_this: T, b : T) => number,
) : Iterable<T> {
    const iteratorA = this[Symbol.iterator]();
    const iteratorB = b[Symbol.iterator]();
    let stepA = iteratorA.next();
    let stepB = iteratorB.next();

    while ( !stepA.done && !stepB.done ) {
        const comparison = stepA.value::comparator(stepB.value);

        if ( comparison < 0 ) {
            yield stepA.value;
            stepA = iteratorA.next();
        } else if ( comparison > 0 ) {
            yield stepB.value;
            stepB = iteratorB.next();
        } else {
            yield stepA.value;
            stepA = iteratorA.next();
            stepB = iteratorB.next();
        }
    }

    if ( !stepA.done ) {
        yield stepA.value;
        yield * iteratorA;
    } else if ( !stepB.done ) {
        yield stepB.value;
        yield * iteratorB;
    }
};

"use strict";

/**
 * Yields the items that aren't equal to the previous item based on a comparator.
 *
 * @this {Iterable<T>}
 * @param comparator The function to assert equality of the items.
 * @ntime O(n)
 * @dspace O(1)
 * @example Basic Usage
 *
 * ```javascript
 * [{
 *   id: 1,
 * }, {
 *   id: 1,
 * }, {
 *   id: 2,
 * }]::uniq(function (b) {
 *   return this.id === b.id;
 * }); // yields { id: 1 } and { id: 2 }
 *
 * [{
 *   id: 1,
 * }, {
 *   id: 2,
 * }, {
 *   id: 1,
 * }]::uniq(function (b) {
 *   return this.id === b.id;
 * }); // yields { id: 1 }, { id: 2 } and { id: 1 }
 * ```
 *
 * @example Unique Characters in a String
 *
 * ```javascript
 * "bcabbbac"::sortAlphabetically()::uniq(equals) // yields "a", "b", "c"
 * ```
*/
export function * uniq <T> (
    comparator : (_this : T, b : T) => boolean,
) : Iterable<T> {
    const iterator = this[Symbol.iterator]();
    const first = iterator.next();

    if ( first.done ) { return; }

    yield first.value;
    let previous = first.value;

    for ( const item of iterator ) {
        if ( !item::comparator(previous) ) { yield item; }
        previous = item;
    }
};

